class Ptainstructor < ActiveRecord::Base

  has_many :courses
  has_many :teachers, :through => :courses
  belongs_to :semester
  has_many :students, :through => :courses

  attr_accessible :first_name,
                  :last_name,
                  :email,
                  :phone,
                  :address,
                  :bio,
                  :semester

  validate :first_name_is_valid
  validate :last_name_is_valid
  validate :email_is_valid
  validate :phone_is_valid
  validate :address_is_valid

  scope :alphabetical, order("last_name asc, first_name asc")
  default_scope alphabetical

  private
  #Adds errors if first_name_is_valid? returns false
  def first_name_is_valid
    errors.add(:name, "Invalid string for first name.") unless first_name_is_valid?
  end

  #Adds errors if last_name_is_valid? returns false
  def last_name_is_valid
    errors.add(:name, "Invalid string for last name.") unless last_name_is_valid?
  end

  #Adds errors if email_is_valid? returns false
  def email_is_valid
    errors.add(:email, "Invalid email address entered.") unless email_is_valid?
  end

  #Adds errors if phone_is_valid? returns false
  def phone_is_valid
    errors.add(:phone, "Invalid phone number entered.") unless phone_is_valid?
  end

  #Adds errors if address_is_valid? returns false
  def address_is_valid
    errors.add(:address, "Invalid address entered.") unless address_is_valid?
  end

  #Returns true if not nil and string
  def not_nil_and_string(str)
    return true unless ((str == nil) or (not str.instance_of? String))
    return false
  end


  public
  #Tests that the ptainstructor's first_name is not nil, a string, and at least 1 character. Return true or false
  def first_name_is_valid?
    return false unless not_nil_and_string(self.first_name)
    return self.first_name.length > 0
  end

  #Tests that the ptainstructor's last_name is not nil, a string, and at least 1 character. Return true or false
  def last_name_is_valid?
    return false unless not_nil_and_string(self.last_name)
    return self.last_name.length >= 0
  end

  #Tests that ptainstructor's email is not nil, string, and meets certain criteria. Returns true or false
