  #Tests that ptainstructor's phone is not nil and is a string. It also checks that it has proper formatting (10 digits with area code) and returns true or false.
  def phone_is_valid?
    return false unless not_nil_and_string(self.phone)
    return false unless ((self.phone =~ /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/) == 0)
    self.phone = formatted_number(self.phone)
    return true
  end

  #Tests that ptainstructor's address is not nil, string, and at least 1 character. Returns true or false
  def address_is_valid?
    return false unless not_nil_and_string(self.address)
    return self.address.length > 0
  end

#Tests that ptainstructor is not linked to any courses in the semester and returns true or false.
  def can_be_deleted?
    courses = Course.where(:semester_id => self.semester, :ptainstructor_id => self.id)
    return courses.length == 0
  end

  #Returns last_name, first_name or just first_name if no last_name exists
  def full_name_last_first
    return self.first_name unless (self.last_name.length > 0)
    return (self.last_name + ", " + self.first_name)
  end

  #Returns first_name, last_name or just first_name if no last_name exists
  def full_name_first_last
    return self.first_name unless (self.last_name.length > 0)
    return (self.first_name + " " + self.last_name)
  end

  #Formats number properly
  def formatted_number(number)
    digits = number.gsub(/\D/, '').split(//)

    if (digits.length == 10)
      digits = digits.join
      '(%s) %s-%s' % [ digits[0,3], digits[3,3], digits[6,4] ]
    end
  end
end
