  private
  def not_nil_and_string(str)
    return true unless ((str == nil) or (not str.instance_of? String))
    return false
  end

  private
  def is_nil_or_empty_string(str)
    return false unless ((str == nil) or (str == ""))
    return true
  end

  private
  def phone_is_valid(phone)
    return false unless not_nil_and_string(phone)
    return false unless ((phone =~ /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/) == 0)
    return true
  end

  def formatted_number(number)
    digits = number.gsub(/\D/, '').split(//)

    if (digits.length == 10)
      digits = digits.join
      '(%s) %s-%s' % [ digits[0,3], digits[3,3], digits[6,4] ]
    end
  end

  public
  def grand_total
    total = 0
    enrollments = Enrollment.find_all_by_student_id(self.id)
    semester = self.semester
    enrollments.each do |enrollment|
      total += enrollment.course.total_fee

      total -= enrollment.scholarship_amount
    end

    total += semester.fee

    return total
  end

end
